class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"post",url:$(t).attr("href")}).done((function(e){let i=parseInt($(t).find("small")[0].innerText);e.deleted?(i-=1,$(t).find("i")[0].classList.remove("like")):(i+=1,$(t).find("i")[0].classList.add("like")),$(t).find("small")[0].innerText=i.toString()})).fail((function(e){console.log("Error in completing the request",e)}))}))}}